import { Loader } from 'astro/loaders';
import { FieldSet, Query } from 'airtable';

interface AirtableLoaderOptions<TFields extends FieldSet = FieldSet> {
    /** The access token. It must have data.records:read access to the base. Defaults to AIRTABLE_TOKEN env var */
    token?: string;
    /** The base ID */
    base: string;
    /** The table name or ID */
    table: string;
    /**
     * Optional query params
     * @see https://airtable.com/developers/web/api/list-records#query
     */
    queryParams?: Query<TFields>["_params"];
}
/**
 * Loads data from an Airtable table.
 */
declare function airtableLoader({ token, base, table, queryParams, }: AirtableLoaderOptions): Loader;

export { type AirtableLoaderOptions, airtableLoader };
